import React, { useState } from "react";
import logo from "./man-person-icon.svg";
import "./Person.css";
import down from './down-arrow.svg';
import deleteButton from "./delete-button.svg";
import editButton from './edit-button.svg'
import up from './up-arrow.svg'


export default function Person(props) {

   const[read , setRead] = useState(false)
   const readTrue=()=>{
     setRead(false)
   }
   const readFalse =()=>{
    setRead(true)
   }
  return (
    <div className="person">
      <div className="person-heading" >
        <div><img src={logo} height="50px" width="50px" /></div>
        <div className="person-name">{props.data["first"]}</div>
        <div className="readButton">{read?<button onClick={readTrue}><img src={up} height="10px" width="10px"/></button>:<button onClick={readFalse}><img src={down} height="10px" width="10px" /></button>}</div>
      </div>
      {read ?<div>
        <div className="person-address">
          <div>
            <div>Age</div>
            <div>{new Date().getFullYear()-new Date(props.data["dob"]).getFullYear()}</div>
          </div>
          <div>
            <div>Gender</div>
            <div>{props.data["gender"]}</div>
          </div>
          <div>
            <div>Country</div>
            <div>{props.data['country']}</div>
          </div>
        </div>
        <div>
          <div>Description</div>
          <div>{props.data["description"]}</div>
        </div>
        <div className="button-edit-delete"><button><img src={deleteButton} width="15px" height="15px" /></button><button><img src={editButton} width="15px" height="15px" /></button></div>
      </div>:""}
    </div>
  );
}
