import React, { useState } from "react";
import "./Person.css";
import down from "./down-arrow.svg";
import deleteButton from "./delete-button.svg";
import editButton from "./edit-button.svg";
import up from "./up-arrow.svg";

export default function Person(props) {
  const [read, setRead] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const readTrue = () => {
    setRead(false);
  };
  const readFalse = () => {
    setRead(true);
  };
  // const dataAge= document.getElementById("age").value
 



  const submitFormHandler = (event) => {
    event.preventDefault();

    const dataAge= document.getElementById("age").value
    let age;
    let difference = new Date().getFullYear() - new Date(props.data["dob"]).getFullYear() 
    if(Number(dataAge) <difference ){
      // age =String(Number(props.data["dob"].slice(0,4))-difference) + props.data["dob"].slice(4) 
      
     // console.log(new Date().getFullYear()-dataAge);
     // console.log(props.data['dob'].slice(5,7))
    //  console.log(props.data['dob'].slice(8,10))
     age= String(new Date().getFullYear()-dataAge)+'-'+String(props.data['dob'].slice(5,7))+'-'+String(props.data['dob'].slice(8,10))
      // console.log("less age"+props.data['dob'])
      console.log(age)
    }else{
      // age =String(Number(props.data["dob"].slice(0,4))+difference) + props.data["dob"].slice(4) 
      age= String(new Date().getFullYear()+dataAge)+'-'+String(props.data['dob'].slice(5,7))+'-'+String(props.data['dob'].slice(8,10))

      console.log(age)
    }

    const data = {
      "id": props.data['id'],
      "first": props.data["first"],
      "last": props.data["last"],
      dob: "1973-10-16",
      gender: "male",
      email: "aidan.wang@example.com",
      picture: "https://randomuser.me/api/portraits/med/men/93.jpg",
      country: "New Zealand",
      description:
        "This character description generator will generate a fairly random description of a belonging to Aidan Wang. However, some aspects of the descriptions will remain the same, this is done to keep the general structure the same, while still randomizing the important details of Aidan Wang.",
    };
  };

  const deleteUserHandler = () => {
    props.deleteUser(props.data["id"]);
  };
  const editButtonHandler = () => {
    setEditMode((editMode) => !editMode);
  };
  return (
    <div className="person">
      <div className="person-heading">
        <div>
          <img src={props.data["picture"]} height="50px" width="50px" />
        </div>
        <div className="person-name">{props.data["first"]}</div>
        <div className="readButton">
          {read ? (
            <button onClick={readTrue}>
              <img src={up} height="10px" width="10px" />
            </button>
          ) : (
            <button onClick={readFalse}>
              <img src={down} height="10px" width="10px" />
            </button>
          )}
        </div>
      </div>
      {read && !editMode ? (
        <div>
          <div className="person-address">
            <div>
              <div>Age</div>
              <div>
                {new Date().getFullYear() -
                  new Date(props.data["dob"]).getFullYear()}
              </div>
            </div>
            <div>
              <div>Gender</div>
              <div>{props.data["gender"]}</div>
            </div>
            <div>
              <div>Country</div>
              <div>{props.data["country"]}</div>
            </div>
          </div>
          <div>
            <div>Description</div>
            <div>{props.data["description"]}</div>
          </div>
          <div className="button-edit-delete">
            <button onClick={deleteUserHandler}>
              <img src={deleteButton} width="15px" height="15px" />
            </button>
            <button onClick={editButtonHandler}>
              <img src={editButton} width="15px" height="15px" />
            </button>
          </div>
        </div>
      ) : (
        ""
      )}
      {read && editMode ? (
        <div>
          <form className="person-address">
            <div>
              <label for="age">Age</label>
              <input
                id="age"
                type="number"
                defaultValue={
                  new Date().getFullYear() -
                  new Date(props.data["dob"]).getFullYear()
                }
              />
              {/* <div>{new Date().getFullYear()-new Date(props.data["dob"]).getFullYear()}</div> */}
            </div>
            <div>
              <label for="gender">Gender</label>
              <input id="gender" type="text" value={props.data["gender"]} />

              {/* <div>{props.data["gender"]}</div> */}
            </div>
            <div>
              <label for="country">Country</label>
              <input id="country" type="text" value={props.data["country"]} />

              {/* <div>{props.data['country']}</div> */}
            </div>
          </form>
          <div>
            <label for="desc">Description</label>
            <textarea
              id="desc"
              type="text"
              row="7"
              value={props.data["description"]}
            ></textarea>

            {/* <div>{props.data["description"]}</div> */}
          </div>
          <div className="button-edit-delete">
            <button onClick={editButtonHandler}>Cancel</button>
            <button onClick={submitFormHandler}>Edit</button>
          </div>
        </div>
      ) : (
        ""
      )}
    </div>
  );
}
